def getSize(trigrams):
    size = 0
    for tri in trigrams:
        size += trigrams[tri]
    return 1.0 * size


def getTrigrams(words,trigrams = 0):
    if (trigrams == 0):
        trigrams = dict()
    for word in words:
        # print word
        word = ("**" + word + "**").lower()
        for i in xrange(len(word) - 2):
            tri = word[i:i+3]
            # print tri
            if tri in trigrams:
                trigrams[tri] += 1
            else:
                trigrams[tri] = 1
    return trigrams

